(1, 'test', '<!DOCTYPE HTML PUBLIC \"-//W3C//DTD HTML 4.01 Transitional//EN\" \"http://www.w3.org/TR/html4/loose.dtd\">\n<html>\n    <head>\n        <!-- Not all email client take care of styles inserted here -->\n        <style type=\"text/css\" media=\"all\">\n            a {\n                text-decoration: none;\n                color: #555555;\n            }\n        </style>\n    </head>\n    <body style=\"margin: 0!important; padding: 0!important\"><div style=\"background-color: #f4f4f4; font-family: Helvetica Neue, Helvetica, Arial, sans-serif; font-size: 14px; color: #666; margin: 0 auto; padding: 0;\"><br />\r\n<table style=\"max-width: 600px; width: 100%; border-collapse: collapse; background-color: #000;\" border=\"0\" width=\"100%\" cellspacing=\"0\" cellpadding=\"0\" align=\"center\" bgcolor=\"#ffffff\">\r\n<tbody>\r\n<tr>\r\n<td style=\"font-family: Helvetica Neue, Helvetica, Arial, sans-serif; font-size: 14px; color: #666;\" valign=\"top\" bgcolor=\"#ffffff\" width=\"100%\">\r\n<div style=\"text-align: left; background-color: #fff;\">\r\n<div style=\"text-align: center;\">\r\n<div style=\"padding: 30px 20px; color: #000; font-size: 28px; background-color: #efefef; border-bottom: 1px solid #ddd; text-align: center;\">Biblio</div>\r\n</div>\r\n<div style=\"padding: 10px 20px 20px 20px; background-color: #fff; line-height: 18px;\">\r\n<p style=\"text-align: center; font-size: small;\"><a href=\"{email_url}\" target=\"_blank\" rel=\"noopener noreferrer\">View this email online</a></p>\r\n<p>Il y a ...</p>\r\n<!-- Footer -->\r\n<div style=\"text-align: center; font-weight: bold; margin: 40px 0 10px;\"> </div>\r\n<div style=\"text-align: center; margin: 10px 0 20px;\"> </div>\r\n<!-- Social -->\r\n<div style=\"text-align: center; color: #888; margin-top: 20px;\"> </div>\r\n<div style=\"text-align: center; color: #888;\">To change your subscription, <a href=\"{profile_url}\" target=\"_blank\" rel=\"noopener noreferrer\">click here</a>.</div>\r\n</div>\r\n</div>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<br /><br /></div></body></html>', '', NULL, '', '2018-01-29 15:16:03', 'new', 0, 0, 0, 1, 0, 'message', 'select * from wp_biblionewsletter where status=\'C\'', 0, '', '', 'This email requires a modern e-mail reader but you can view the email online here:\r\n\r\n{email_url}.\r\n\r\nThank you, Biblio\r\nTo change your subscription follow:\r\n{profile_url}.', NULL, 1517238000, '', 'a:3:{s:14:\"lists_operator\";s:2:\"or\";s:6:\"status\";s:1:\"C\";s:8:\"wp_users\";s:1:\"0\";}', 0, 0, '', 0);
